package com.danmin.sopt_3.fragment

import android.os.Bundle
import androidx.fragment.app.Fragment
import android.view.LayoutInflater
import android.view.View
import android.view.ViewGroup
import androidx.recyclerview.widget.LinearLayoutManager
import com.danmin.sopt_3.R
import com.danmin.sopt_3.feedRecycler.FeedAdapter
import com.danmin.sopt_3.feedRecycler.FeedData
import kotlinx.android.synthetic.main.fragment_home.*

class HomeFragment : Fragment() {
    lateinit var feedAdapter: FeedAdapter
    val datas = mutableListOf<FeedData>()

    override fun onCreateView(
        inflater: LayoutInflater, container: ViewGroup?,
        savedInstanceState: Bundle?
    ): View? {
        return inflater.inflate(R.layout.fragment_home, container, false)
    }

    override fun onViewCreated(view: View, savedInstanceState: Bundle?) {
        super.onViewCreated(view, savedInstanceState)
        feedAdapter = FeedAdapter(view.context)
        rv_home.run {
            adapter = feedAdapter
            layoutManager = LinearLayoutManager(view.context, LinearLayoutManager.VERTICAL, false)
        }
        loadDatas()
    }

    private fun loadDatas() {
        datas.apply {
            add(
                FeedData(
                    userName = "이정민",
                    img_profile = "https://avatars1.githubusercontent.com/u/50590192?s=460&u=e6a104455a4e61cb4b4693e1be8debc48bb955f6&v=4",
                    img_contents = "https://img.insight.co.kr/static/2018/09/15/700/747cl3tq51cz06846r5u.jpg"
                )
            )
            add(
                FeedData(
                    userName = "GitHub",
                    img_profile = "data:image/png;base64,",
                    img_contents = "data:image/png;base64,"
                )
            )
        }
        feedAdapter.datas = datas
        feedAdapter.notifyDataSetChanged()
    }
}
